from pyrogram import Clientfrom configurations.config import BOT_TOKEN, API_ID, API_HASHimport osBOT_TOKEN = os.environ.get("BOT_TOKEN", BOT_TOKEN)API_ID = int(os.environ.get("API_ID", API_ID))API_HASH = os.environ.get("API_HASH", API_HASH)if __name__ == "__main__":    handlers = dict(        root="handlers"    )    bot = Client(        "YoutubeToMP3",        bot_token=BOT_TOKEN,        api_hash=API_HASH,        api_id=API_ID,        plugins=handlers    )    bot.run()